#Counts the number of times a pattern is detected

def PatternCount(text, pattern):
	count = 0
	start = 0

	while True:
		start = text.find(pattern, start) + 1
		if start > 0:
			count += 1 
		else:
			return count

#Testing
assert(PatternCount('attg','at')) == 1
assert(PatternCount('atgat','cat')) == 0
input = ""
check = "GGGCGCGGG"
assert(PatternCount(input,check)) == 37
input = "AGGGCGCGTCTGGGCGCGGGGCGCGGGG"
check = "GGGC"
assert(PatternCount(input,check)) == 3
input = ""
check = "CCCCGTGCC"
assert(PatternCount(input,check)) == 37