#Converts a DNA sequence into RNA
#Converts any T in a sequence into U
def convertRNA(seq):
	rna = ""

	for i in range(len(seq)):
		if seq[i] == 'T':
			rna += 'U'
		else:
			rna += seq[i]

	return rna

#Assertion tests
text = "GATGGAACTTGACTACGTAAATT"
assert(convertRNA(text)) == 'GAUGGAACUUGACUACGUAAAUU'

text = ""
assert(convertRNA(text)) == ''
