#Counts anbd returns the number of bases in a sequence
def convertRNA(seq):
	rna = ""

	for i in range(len(seq)):
		if seq[i] == 'T':
			rna += 'U'
		else:
			rna += seq[i]

	return rna

text = "GATGGAACTTGACTACGTAAATT"
#GAUGGAACUUGACUACGUAAAUU
print(convertRNA(text))

